func Test_reduceIndex(t *testing.T) {

	tests := []struct {
		name  string
		index []be
		want  []be
	}{

		{
			name:  "2",
			index: []be{{1, 2}, {3, 6}},
			want:  []be{{1, 6}},
		}}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := reduceIndex(tt.index); !reflect.DeepEqual(got, tt.want) {
				t.Errorf("reduceIndex() = %v, want %v", got, tt.want)
			}
		})
	}
}